mapper.select:
        SELECT *
        
            ,from_unixtime(create_date/1000,"%Y") AS year,MONTH(from_unixtime(create_date/1000)) AS month
        

        FROM ms_article
        
            
                AND category_id=#{pageParams.categoryId}
            
            
                AND id IN
                
                    #{articleId}
                
            
        
        
            HAVING year=#{pageParams.year} AND month=#{pageParams.month}
        
        ORDER BY ${order1} DESC,${order2} DESC LIMIT #{offset},#{rows}
